mapper.select:
SELECT p.p_no , p.t_no , p.m_qr , p.p_state , p.p_ok , p.p_comment , t.t_name , t.t_realP , (SELECT MAX(r_ct) FROM round WHERE m_qr = p.m_qr AND p.p_no = p_no AND t.t_no = p.t_no) AS r_ct FROM pt p LEFT JOIN trainer t on p.t_no = t.t_no WHERE p.m_qr = #{m_qr}
AND t.t_name LIKE CONCAT ('%', #{searchMember},'%')
UNION SELECT p.p_no , p.t_no , p.m_qr , p.p_state , p.p_ok , p.p_comment , t.t_name , t.t_realP , (SELECT MAX(r_ct) FROM round WHERE m_qr = p.m_qr AND p.p_no = p_no AND t_no = p.t_no) AS r_ct FROM trainer t LEFT JOIN pt p on p.t_no = t.t_no WHERE p.m_qr = #{m_qr}
AND t.t_name LIKE CONCAT ('%', #{searchMember},'%')

SELECT  
	 (SELECT COUNT(CASE WHEN e_part='A' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
	 	 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS A
	,(SELECT COUNT(CASE WHEN e_part='B' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS B
	,(SELECT COUNT(CASE WHEN e_part='C' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS C
	,(SELECT COUNT(CASE WHEN e_part='D' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS D
	,(SELECT COUNT(CASE WHEN e_part='E' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS E
	,(SELECT COUNT(CASE WHEN e_part='F' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS F
	,(SELECT COUNT(CASE WHEN e_part='G' THEN 1 END)
	    FROM exercise
	   WHERE t_no = #{t_no}
		 AND p_no = #{p_no}
		 AND m_qr = #{m_qr}) AS G
FROM DUAL

	SELECT * FROM round
	 WHERE t_no = #{t_no}
	   AND p_no = #{p_no}
	   AND m_qr = #{m_qr}
  ORDER BY r_no DESC
