mapper.select:
SELECT p.p_no , p.t_no , p.m_qr , p.p_state , p.p_ok , p.p_comment , m.m_name , m.m_realP FROM pt p LEFT JOIN members m on p.m_qr = m.m_qr WHERE p.t_no = #{t_no}
AND m.m_name like CONCAT ('%', #{searchMember},'%')
AND p.p_state NOT IN ('C') UNION SELECT p.p_no , p.t_no , p.m_qr , p.p_state , p.p_ok , p.p_comment , m.m_name , m.m_realP FROM members m LEFT JOIN pt p on p.m_qr = m.m_qr WHERE p.t_no = #{t_no}
AND m.m_name LIKE CONCAT ('%', #{searchMember},'%')
AND p.p_state NOT IN ('C')

mapper.update:
UPDATE pt
   SET p_state = 'C'
 WHERE p_no = #{p_no}

mapper.select:
SELECT  
	 (SELECT COUNT(CASE WHEN e_part='A' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS A
	,(SELECT COUNT(CASE WHEN e_part='B' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS B
	,(SELECT COUNT(CASE WHEN e_part='C' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS C
	,(SELECT COUNT(CASE WHEN e_part='D' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS D
	,(SELECT COUNT(CASE WHEN e_part='E' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS E
	,(SELECT COUNT(CASE WHEN e_part='F' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS F
	,(SELECT COUNT(CASE WHEN e_part='G' THEN 1 END)
	   FROM exercise
	  WHERE t_no = #{t_no}
		AND p_no = #{p_no}
		AND m_qr = #{m_qr}) AS G
FROM DUAL

mapper.update:

UPDATE pt 
   SET p_kg   = #{p_kg}
	 , p_bone = #{p_bone}
	 , p_fat  = #{p_fat}
 WHERE m_qr   = #{m_qr}

UPDATE pt
   SET p_comment = #{p_comment}
 WHERE m_qr = #{m_qr}

mapper.insert:

INSERT INTO round
(
	  t_no
	, m_qr
	, p_no
	, r_ct
	, r_date
)VALUES
(
	  #{t_no}
	, #{m_qr}
	, #{p_no}
	, #{r_ct}
	, now()
)


SELECT MAX(r_no) FROM round


INSERT INTO exercise
(
	  t_no
	, m_qr
	, p_no
	, r_no
	, e_name
	, e_set
	, e_cnt
	, e_kg
	, e_time
	, e_part
	, e_rest
)VALUES
(
	  #{t_no}
	, #{m_qr}
	, #{p_no}
	, #{r_no}
	, #{e_name}
	, #{e_set}
	, #{e_cnt}
	, #{e_kg}
	, #{e_time}
	, #{e_part}
	, #{e_rest}
)


SELECT MAX(e_no) FROM exercise

mapper.update:
UPDATE exercise
   SET  e_name = #{e_name}
	  , e_set  = #{e_set}
	  , e_cnt  = #{e_cnt}
	  , e_kg   = #{e_kg}
	  , e_time = #{e_time}
	  , e_part = #{e_part}
	  , e_rest = #{e_rest}
  WHERE e_no   = #{e_no}
